<?php// Exit if accessed directlyif (!defined('ABSPATH')) exit;global $smof_data,  $theme_text_domain;/** * Archive Template * * * @file           archive.php * @package        Parasponsive * @author         GoGetThemes * @copyright      2013 GoGetThemes * @license        license.txt * @version        Release: v1.0 */?><?php get_header(); ?><!-- =========================POSTS============================= --><section id="blog">    <?php    $args = array(        'cat' => get_query_var('cat'),        'paged' => get_query_var('paged') ? get_query_var('paged') : 1,        'tax_query' => array(            array(                'taxonomy' => 'post_format',                'field' => 'slug',                'terms' => array('post-format-link', 'post-format-image', 'post-format-quote'),                'operator' => 'NOT IN'            ))    );    query_posts($args);    if (have_posts()) : ?>    <div class="container margin-top-20 <?php if($smof_data['titles_position']==1) echo "text_center"; else echo "text_left";?>">        <h3><?php if (is_day()) : ?>                <?php printf(__('Daily Archives: %s', 'GoGetThemes'), '<span>' . get_the_date() . '</span>'); ?>            <?php elseif (is_month()) : ?>                <?php printf(__('Monthly Archives: %s', 'GoGetThemes'), '<span>' . get_the_date('F Y') . '</span>'); ?>            <?php elseif (is_year()) : ?>                <?php printf(__('Yearly Archives: %s', 'GoGetThemes'), '<span>' . get_the_date('Y') . '</span>'); ?>            <?php else : ?>                <?php printf(__($smof_data['blog_menu_name'])); ?>            <?php endif; ?></h3>        <div class="lines sub_title"><span class="sub_title_text"><?php printf(__($smof_data['blog_subtitle'])); ?></span></div>    </div>    <div class="container">        <div class="row-fluid">            <div class="span9">                <ul id="bloglist" class="nostyle">                    <?php while (have_posts()) : the_post();                        $post_format = get_post_format();                        ?>                        <!-- ========== POST SECTION ========== -->                        <li class="post">                            <h2 class="<?php if($smof_data['titles_position']==1) echo "text_center"; else echo "text_left";?>"><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h2>                            <?php if($post_format == "gallery"):  ?>                            <ul class="blog-gallery">                                <?php                                $gallery = get_field("gallery");                                if($gallery): ?>                                    <?php foreach($gallery as $img){                                        echo '<li><img style="max-width:100%;" src="' . $img['url'] . '" /></li>';                                    }                                else:                                    $gallery = get_post_gallery_images( get_the_ID() );                                    if(!empty($gallery)){                                        foreach($gallery as $image_src){                                            echo '<li><img src="' . $image_src . '" /></li>';                                        }                                    }                                endif; ?>                                <?php echo "</ul>"; ?>                                <?php else: ?>                                    <?php if (has_post_thumbnail()): ?>                                        <div class="post_thumb">                                            <a href="<?php the_permalink(); ?>" title="<?php the_title_attribute(); ?>">                                                <?php the_post_thumbnail('blog-thumb'); ?>                                            </a>                                        </div>                                    <?php endif; ?>                                <?php endif;?>                            <div class="entry">                                <?php the_excerpt(); ?>                            </div>                            <ul class="post_info">                                <li class="blog_date">                                    <span class="circ"><?php the_time('d'); ?></span> <?php the_time('F Y'); ?>                                </li>                                <li class="comments">                                    <?php if ('open' == $post->comment_status) : ?>                                        <!-- If comments are open, but there are no comments. -->                                        <span class="circ"><?php comments_popup_link('0', '1', '%'); ?></span> <?php comments_popup_link(__('Comments','GoGetThemes')); ?>                                    <?php else : // comments are closed ?>                                        <span class="circ"><?php printf(__('OFF','GoGetThemes'));?></span> <?php printf(__('Comment.','GoGetThemes'));?>                                    <?php endif; ?>                                </li>                                <li class="read_more">                                    <a href="<?php the_permalink(); ?>"><span><i class="icon-chevron-right"></i></span><?php printf(__('Read more','GoGetThemes'));?></a>                                </li>                            </ul>                        </li>                    <?php endwhile; ?>                </ul>                <?php if(isset($smof_data["blog_load"]) && $smof_data["blog_load"] == "ajax" ): ?>                <div id="blogmore" class="text_center">                    <a href="" class="load_more"><?php printf(__('Load more', 'GoGetThemes')); ?><br/><i class="icon-chevron-down"></i></a>                </div>                <?php elseif(function_exists('wp_paginate')): ?>                    <?php wp_paginate(); ?>                <?php endif; ?>            </div>            <?php get_sidebar('blog'); ?>        </div>        <?php else : ?>            <h1 class="title-404"><?php _e('404 &#8212; Fancy meeting you here!', 'GoGetThemes'); ?></h1>            <p><?php _e('Don&#39;t panic, we&#39;ll get through this together. Let&#39;s explore our options here.', 'GoGetThemes'); ?></p>            <h6><?php _e('You can return', 'GoGetThemes'); ?> <a href="<?php echo home_url(); ?>/" title="<?php esc_attr_e('Home', 'GoGetThemes'); ?>"><?php _e('&larr; Home', 'GoGetThemes'); ?></a> <?php _e('or search for the page you were looking for', 'GoGetThemes'); ?></h6>            <?php get_search_form(); ?>        <?php        endif; ?>    </div>    <a href="#home" class="back2top"><i class="icon-chevron-up"></i></a></section>         <?php get_footer(); ?>