<?php/** Template Name: Paralax home page*/// Exit if accessed directlyif (!defined('ABSPATH')) exit;global $smof_data;$intro_class = $smof_data["first_intro"] ? "intro-on" : "intro-off";get_header();?><!-- ========================= SECTIONS ============================= --><section id="pages" class="<?php echo $intro_class; ?>">    <ul class="nostyle">        <?php get_template_part("section-home") ?>        <?php $smof_data['homepage_blocks'];        $i = 0;        foreach ($smof_data['homepage_blocks']['enabled'] as $block) {            $i++;            switch ($block) {                case 'Service' :                    get_template_part("section-service");                    break;                case 'Portfolio' :                    get_template_part("section-portfolio");                    break;                case 'Pricing Table' :                    get_template_part("section-prices");                    break;                case 'About Us' :                    get_template_part("section-team");                    break;                case 'Contact Us' :                    get_template_part("section-contacts");                    break;                case 'WooSection' :                    get_template_part("section-woo");                    break;                case 'Blank1' :                    get_template_part("section-blank1");                    break;                case 'Blank2' :                    get_template_part("section-blank2");                    break;                case 'Blank3' :                    get_template_part("section-blank3");                    break;                default :                    break;            } // end of section type switch        } // end of foreach        ?>    </ul></section><?php get_footer(); ?>