d'] as $port_category_slug => $port_category) {                                     		if($port_category == 'placebo'){                                        		continue;                                       		}                                        	echo '<li><a href="#filter" data-option-value=".' . $port_category_slug . '">' . $port_category . '</a></li>';                                    	}                                    }                                    ?>                                </ul>                            </section>                <div id="container">                    <?php $port_args = array(                        'post_type' => 'portfolio',                        'showposts' => $smof_data['port_posts_num']                    );                    $the_query = new WP_Query($port_args);                    while ($the_query->have_posts()) :                        $the_query->the_post();?>                        <div <?php post_class(); ?>>                            <div class="iso_inner">                                <?php $thumb = wp_get_attachment_image_src(get_post_thumbnail_id($post->ID), 'full');                                $url = $thumb['0']; ?>                                <img src="<?php echo $url; ?>" alt="" width="280" height="280"/>                                <div class="over_box">                                    <div class="over_box_pad">                                        <div class="over_box_inner">                                            <?php if ($smof_data['port_item_title_mod'] == 1) { ?>                                                <span class="portfolio_name"><?php the_title(); ?></span>                                            <?php } ?>                                            <?php if ($smof_data['port_item_desc_mod'] == 1) { ?>                                                <span                                                    class="portfolio_desc"><?php the_time('F d, Y'); ?></span>                                            <?php } ?>                                            <?php if ($smof_data['port_item_pp_mod'] == 1) { ?>                                                <a class="portfolio_pop" href="<?php the_permalink(); ?>"><i                                                        class="icon-search"></i></a>                                            <?php } ?>                                        </div>                                    </div>                                </div>                            </div>                        </div>                    <?php endwhile; ?>                </div>                <div class="text_center">                    <a href="<?php echo get_template_directory_uri() . '/ajax.php'; ?>" class="load_more"><?php printf(__('Load more', 'GoGetThemes')); ?><br/><i                            class="icon-chevron-down"></i></a>                </div>            <?php endwhile; ?>        <?php else : ?>            <h1 class="title-404"><?php _e('404 &#8212; Fancy meeting you here!', 'GoGetThemes'); ?></h1>            <p><?php _e('Don&#39;t panic, we&#39;ll get through this together. Let&#39;s explore our options here.', 'GoGetThemes'); ?></p>            <h6><?php _e('You can return', 'GoGetThemes'); ?> <a href="<?php echo home_url(); ?>/"                                                                title="<?php esc_attr_e('Home', 'GoGetThemes'); ?>"><?php _e('&larr; Home', 'GoGetThemes'); ?></a> <?php _e('or search for the page you were looking for', 'GoGetThemes'); ?>            </h6>            <?php get_search_form(); ?>        <?php endif; ?>    </div>    <!-- end of #content --></div><?php get_footer(); ?>