<?php// Exit if accessed directlyif (!defined('ABSPATH')) exit;/** * Single Portfolio * * * @file           single-portfolio.php * @package        Parasponsive * @author         GoGetThemes * @copyright      2013 GoGetThemes * @license        license.txt * @version        Release: v1.0 * */if(isset($_GET["sender"]) && $_GET["sender"] == "prettyphoto") { $ajax_request = true; }if(!$ajax_request): // in ajax box we don't need this    get_header();    echo '<section id="single-portfolio">';endif;?>    <div id="portfolio_box">        <?php if (have_posts()) : ?>            <?php while (have_posts()) : the_post(); ?>                <?php $slider = get_field('image_slider');                if ($slider) {                    ?>                    <!-- prev/next links -->                    <div class="cycle-slideshow" data-cycle-fx=scrollHorz                         data-cycle-timeout=0                         data-cycle-prev=".cycle-prev"                         data-cycle-next=".cycle-next">                        <div class="cycle-controls">                            <div class="cycle-prev"><i class="icon-chevron-left"></i></div>                            <div class="cycle-next"><i class="icon-chevron-right"></i></div>                        </div>                        <?php                        foreach ($slider as $slide) {                            echo '<img src="' . $slide['url'] . '" alt="' . $slide['alt'] . '" />';                        }                        ?>                    </div>                <?php } ?>                <div class="entry">                    <h5><?php the_title(); ?></h5>                    <?php the_content(); ?>                </div>                <script>                    jQuery(document).ready(function ($) {                        $('#portfolio_box .cycle-slideshow').cycle({                            swipe: true                        });                        /*if ( $('#niceScrollStatus').val() === 1 ){                         $("#portfolio_box").find('.entry').niceScroll();                         }*/                        $('#portfolio_box .tabs-wrapper').each(function () {                            $(this).find(".tab_content").hide(); //Hide all content                            $(this).find("ul.tabs li:first").addClass("active").show(); //Activate first tab                            $(this).find(".tab_content:first").show(); //Show first tab content                        });                        //On Click Event                        $("#portfolio_box ul.tabs li").click(function (e) {                            $(this).parents('.tabs-wrapper').find("ul.tabs li").removeClass("active"); //Remove any "active" class                            $(this).addClass("one"); //Add "active" class to selected tab                            $(this).parents('.tabs-wrapper').find(".tab_content").hide(); //Hide all tab content                            var activeTab = $(this).find("a").attr("href"); //Find the href attribute value to identify the active tab + content                            $(this).parents('.tabs-wrapper').find(activeTab).fadeIn(); //Fade in the active ID content                            e.preventDefault();                        });                        $("#portfolio_box ul.tabs li a").click(function (e) {                            e.preventDefault();                        });                    });                </script>                <style>                    .video-shortcode iframe {                        max-width: 100%;                    }                </style>            <?php endwhile; ?>        <?php else : ?>            <h1 class="title-404"><?php _e('404 &#8212; Fancy meeting you here!', 'GoGetThemes'); ?></h1>            <p><?php _e('Don&#39;t panic, we&#39;ll get through this together. Let&#39;s explore our options here.', 'GoGetThemes'); ?></p>            <h6><?php _e('You can return', 'GoGetThemes'); ?> <a href="<?php echo home_url(); ?>/"                                                                 title="<?php esc_attr_e('Home', 'GoGetThemes'); ?>"><?php _e('&larr; Home', 'GoGetThemes'); ?></a> <?php _e('or search for the page you were looking for', 'GoGetThemes'); ?>            </h6>            <?php get_search_form(); ?>        <?php endif; ?>    </div><?phpif(!$ajax_request){    echo '</section>';    get_footer();} ?>